import React, { Component } from 'react';
import Layout from "../components/layout";
import Highcharts from 'highcharts';
import ReactHighcharts from 'react-highcharts';

import auth from "../services/authService";
import crypto from "../services/cryptoService";

import loading from "../src/91.gif";
import cryptoImg from "../src/crypto-text.png";

const chartOptions = {
	chart: { zoomType: 'x' },
	xAxis: {
		type: 'datetime',
		dateTimeLabelFormats: { day: '%d-%m-%Y - %h:%m' },
		title: { text: 'Date' }
	},
	yAxis: {
		title: { text: 'Exchange rate' }
	},
	legend: { enabled: false },
	plotOptions: {
		area: {
			fillColor: {
				linearGradient: {
					x1: 0,
					y1: 0,
					x2: 0,
					y2: 1
				},
				stops: [
					[0, "#7cb5ec"],
					[0.5, "#7cb5ec80"],
					[0.9, "#7cb5ff33"]
				]
			},
			marker: { radius: 2 },
			lineWidth: 1,
			states: { hover: { lineWidth: 1 } },
			threshold: null
		}
	}
}

export default class SavedData extends Component {
    state = {
        user: {},
        jwt: "",
        data: {}
    }

    async componentDidMount(){
        const user = auth.getCurrentUser();
        const jwt = auth.getJwt();
        await crypto.getSavedData(user._id);
        this.setState({ user, jwt });
    }

    render() {
        return (
            <Layout>
                <h1>HELLO!</h1>
            </Layout>
        )
    }
}
