import React, { Component } from 'react';
import ReactHighcharts from 'react-highcharts';
import Highcharts from 'highcharts';

const chartOptions = {
    chart: {zoomType: 'x'},
    xAxis: { 
            type: 'datetime',
            dateTimeLabelFormats: {day: '%d-%m-%Y - %h:%m'},
        title: {text: 'Date'}
        },
    yAxis: {
        title: {text: 'Exchange rate'}
    },
    legend: {enabled: false},
    plotOptions: {
        area: {
            fillColor: {
                linearGradient: {
                    x1: 0,
                    y1: 0,
                    x2: 0,
                    y2: 1
                },
                stops: [
                    [0, "#7cb5ec"],
                    [0.5, "#7cb5ec80"],
                    [0.9, "#7cb5ff33"]
                ]
            },
            marker: {radius: 2},
            lineWidth: 1,
            states: {hover: {lineWidth: 1}},
            threshold: null
        }
    }
}

export default class Chart extends Component {
    state = {
        data: {
            cryptocurrencies: [],
            currencies: []
        }
    }

    componentDidMount(){
        this.setState({data:this.props.data})
        console.log(this.props.data);
    }

    render() {
        return (
            <div>
                {this.state.data.cryptocurrencies.length > 0 ? 
                <div>
                    {console.log("HERE")}
                    <div className="col-md-3">
                    <div className="form-group">
                        <select name="Cryptocurrency" onChange={(e) => this.changeGraphCryptocurrency(e,this.state.data.index)} defaultValue={this.state.data.item.cryptocurrency} className="form-control">
                            {this.state.data.cryptocurrencies.map(i => (<option value={i.id} key={i.id}>{i.name} - {i.symbol}</option>))}
                        </select>
                        <select name="Currency" onChange={(e) => this.changeGraphCryptocurrency(e,this.state.data.index)} defaultValue={this.state.data.item.currency} className="form-control">
                            {this.state.data.currencies.map(i => (<option value={i.id} key={i.id}>{i.name} - {i.symbol}</option>))}
                        </select>
                        <input type="checkbox" onChange={(e) => this.changeGraphLive(e,this.state.data.index)} defaultValue={this.state.data.item.live} className="form-control"/>
                        <button className="btn btn-success" onClick={() => this.refreshSingle(this.state.data.index)}>REFRESH</button>
                        <button className="btn btn-warning" onClick={() => this.refreshSingle(this.state.data.index)}>SEARCH</button>
                        <button className="btn btn-danger" onClick={() => this.removeGraph(this.state.data.index)}>DELETE</button>
                        <button className="btn btn-primary" onClick={() => this.saveGraph(this.state.data.index)}>SAVE</button>
                    </div>
                </div>
                <div className="col-md-9 text-center">
                    {!this.state.data.loading ? <ReactHighcharts key={item.id} highcharts={Highcharts} config={item.chartOptions} ref="chart" oneToOne={true}/> : <img src={loading}/>}
                </div>
                </div> : "" }
            </div>
        )
    }
}
