import React, { Component } from 'react';
import Link from "./link";

import auth from "../services/authService";
import crypto from "../src/crypto.png";

export default class Navbar extends Component {
    state = {
        user: {}
    }

    componentDidMount(){
        const user = auth.getCurrentUser();
        console.log(user);
		this.setState({ user });
    }

    handleLogout = () => {
        auth.logout();
        window.location = "/login";
    }

    render() {
        return (
            <div>
                <nav className="navbar navbar-expand-md navbar-light">
                    <Link href="/">
                        <a className="navbar-brand" href="#"><img src={crypto}/></a>
                    </Link>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNav">
                        <ul className="navbar-nav">
                            <li className="nav-item">
                                <Link href="/">
                                    <a className="nav-link"><strong>Dashboard</strong></a>
                                </Link>
                            </li>
                            <li className="nav-item">
                                <Link href="/customdata">
                                    <a className="nav-link"><strong>Custom Data Graphs</strong></a>
                                </Link>
                            </li>
                            <li className="nav-item">
                                <Link href="/saveddata">
                                    <a className="nav-link"><strong>Saved Data</strong></a>
                                </Link>
                            </li>
                            {this.state.user.isAdmin && 
                                <li className="nav-item">
                                    <Link href="/admin">
                                        <a className="nav-link text-success"><strong>Administration Panel</strong></a>
                                    </Link>
                                </li>}
                        </ul>
                        <li className="nav-item ml-auto">
                            <span className="nav-link">{this.state.user.name} <a href="#" className="text-danger" style={{display:"inline-block"}} onClick={this.handleLogout}><strong><i className="fas fa-power-off"/></strong></a></span>
                        </li>
                    </div>
                </nav>
            </div>
        )
    }
}
