import React, { Component } from 'react';
import Layout from "../components/layout";
import Highcharts from 'highcharts';
import ReactHighcharts from 'react-highcharts';

import auth from "../services/authService";
import crypto from "../services/cryptoService";

import loading from "../src/91.gif";
import cryptoImg from "../src/crypto-text.png";

const chartOptions = {
	chart: { zoomType: 'x' },
	xAxis: {
		type: 'datetime',
		dateTimeLabelFormats: { day: '%d-%m-%Y - %h:%m' },
		title: { text: 'Date' }
	},
	yAxis: {
		title: { text: 'Exchange rate' }
	},
	legend: { enabled: false },
	plotOptions: {
		area: {
			fillColor: {
				linearGradient: {
					x1: 0,
					y1: 0,
					x2: 0,
					y2: 1
				},
				stops: [
					[0, "#7cb5ec"],
					[0.5, "#7cb5ec80"],
					[0.9, "#7cb5ff33"]
				]
			},
			marker: { radius: 2 },
			lineWidth: 1,
			states: { hover: { lineWidth: 1 } },
			threshold: null
		}
	}
}

export default class SavedData extends Component {
    state = {
        user: {},
        jwt: "",
        data: {
            graphs: []
        },
        loaded: false,
        graphs: []
    }

    async componentDidMount(){
        const user = auth.getCurrentUser();
        const jwt = auth.getJwt();
        
        this.setState({ user, jwt, data, loaded: true, graphs });
    }

    refreshData = async () => {

    }

    render() {
        return (
            <Layout>
                <div className="w-50 mx-auto text-center pt-5">
					<img src={cryptoImg} />
					<h1 style={{fontFamily: "Courier New"}}><strong>Saved Data</strong></h1>
					<hr/>
                    {this.state.loaded && <div className="col-md-12 mt-3">
                        <p><strong>Refresh</strong></p>
                        <button className="btn btn-success" onClick={this.refreshData}><i className="fas fa-sync"/></button>
                    </div>}
                </div>
                {this.state.loaded ?
                    <div className="text-center">
                        {this.state.graphs.length > 0 ?
                            this.state.graphs.map((item, index) =>
                                <div key={item.id} className="pt-3">
                                    <hr/>
                                    <img src={item.image} />
                                    <div className="row mt-4">
                                        <div className="col-md-9 text-center">
                                            {!this.state.loading ? <ReactHighcharts key={item.id} highcharts={Highcharts} config={item.chartOptions} ref="chart" oneToOne={true} /> : <img src={loading} />}
                                        </div>
                                        <div className="col-md-3">
                                            <div className="form-group">
                                                <div className="pt-2">
													<input type="text" value={item.cryptocurrency} className="form-control text-center" disabled/>
												</div>
												<div className="pt-2">
                                                <input type="text" value={item.currency} className="form-control text-center" disabled/>
												</div>
                                                <div className="row">
                                                    <div className="col-md-12 mt-3">
                                                        <p><strong>Compare with current data</strong></p>
                                                        <input type="checkbox" onChange={(e) => this.changeGraphLive(e, index)} defaultValue={item.live} className="form-control" />
                                                    </div>
                                                    <div className="col-md-12 mt-3">
                                                        <p><strong>Delete</strong></p>
                                                        <button className="btn btn-danger" onClick={() => this.removeGraph(index)}><i className="fas fa-minus"/></button>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                        
                                    </div>
                                </div>
                            ) : ""}
                    </div>
				: <div className="w-50 mx-auto text-center" style={{paddingTop: "30vh"}}><img src={loading} /></div>}
            </Layout>
        )
    }
}
