import React, { Component } from 'react';
import Link from "../components/link";

import Layout from "../components/layout";
import auth from "../services/authService";
import crypto from "../services/cryptoService";
import cryptoImg from "../src/crypto-text.png";

class Index extends Component{
    state = {
        user: {}
    }

    async componentDidMount() {
		const user = auth.getCurrentUser();
        this.setState({user});
    }

    render(){
        if(process.browser && !auth.getCurrentUser()) {
            window.location.replace("/login");
        }
        else return (
            <Layout>
                <div className="w-75 mx-auto text-center" style={{paddingTop: "15vh"}}>
					<img src={cryptoImg} />
					<h1 style={{fontFamily: "Courier New"}}><strong>A solution for your investments</strong></h1>
					<hr/>
                    <div className="w-50">
                        <img src="https://steemitimages.com/DQmV75cQgvWMEXBdDrH7vJfMLzyAY8KxLvAsBrViSyendvB/coins-2.png" className="text-center"/>
                    </div>
                    <div className="mt-5">
                        <Link href="/customdata">
                            <a className="btn btn-primary"><strong>Build your custom graphs</strong></a>
                        </Link>
                    </div>
				</div>
            </Layout>
        );
    }
}
  
export default Index;
