import Highcharts from 'highcharts';
import ReactHighcharts from 'react-highcharts';

const chartOptions = {
    chart: {zoomType: 'x'},
    xAxis: { 
            type: 'datetime',
            dateTimeLabelFormats: {day: '%d-%m-%Y - %h:%m'},
        title: {text: 'Date'}
        },
    yAxis: {
        title: {text: 'Exchange rate'}
    },
    legend: {enabled: false},
    plotOptions: {
        area: {
            fillColor: {
                linearGradient: {
                    x1: 0,
                    y1: 0,
                    x2: 0,
                    y2: 1
                },
                stops: [
                    [0, "#7cb5ec"],
                    [0.5, "#7cb5ec80"],
                    [0.9, "#7cb5ff33"]
                ]
            },
            marker: {radius: 2},
            lineWidth: 1,
            states: {hover: {lineWidth: 1}},
            threshold: null
        }
    }
}

const Chart = (props) => {
    return (
        <ReactHighcharts key={this.props.item.id} highcharts={Highcharts} config={item.chartOptions} ref="chart" oneToOne={true}/>
    )
}
