import React, { Component } from 'react';
import Head from "next/head";
import Navbar from "../components/navbar";
import Footer from "../components/footer";
import io from "socket.io-client";

import auth from "../services/authService";
import crypto from "../services/cryptoService";
import "../static/style.css";

class Index extends Component{
    state = {
        user: {},
        data: {
            data: [],
            timestamp: ""
        }
    }

    componentDidMount() {
		const user = auth.getCurrentUser();
        this.setState({user});
    }

    handleReq = async () => {
        await crypto.getCurrencies().then(res => this.setState({data: res.data}));
        console.log(this.state.data);
    }
    
    handleLogout = () => {
        auth.logout();
        window.location.replace("/login");
    }

    render(){
        if(process.browser && !auth.getCurrentUser()) {
            window.location.replace("/login");
        }
        else return (
            <div className="main">
                <Head>
                    <title>Login</title>
                    <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css" integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T" crossorigin="anonymous"/>
                </Head>
                <Navbar/>
                <div className="container">
                    <p>Hello {this.state.user.name}</p>
                    <button onClick={this.handleReq}>GET ASSETS</button>
                    <div className="row">
                        {this.state.data.data.map(item => (
                            <div className="col-sm-2 border" key={item.id}>
                                <p>{item.name}</p>
                                <p>{item.symbol}</p>
                            </div>
                        ))}
                    </div>
                </div>
                <Footer/>
            </div>
        );
    }
}
  
export default Index;
