import React, { Component } from 'react';
import Layout from "../components/layout";
import Highcharts from 'highcharts';
import ReactHighcharts from 'react-highcharts';

import auth from "../services/authService";
import crypto from "../services/cryptoService";

import loading from "../src/91.gif";
import cryptoImg from "../src/crypto-text.png";

const chartOptions = {
	chart: { zoomType: 'x' },
	xAxis: {
		type: 'datetime',
		dateTimeLabelFormats: { day: '%d-%m-%Y - %h:%m' },
		title: { text: 'Date' }
	},
	yAxis: {
		title: { text: 'Exchange rate' }
	},
	legend: { enabled: false },
	plotOptions: {
		area: {
			fillColor: {
				linearGradient: {
					x1: 0,
					y1: 0,
					x2: 0,
					y2: 1
				},
				stops: [
					[0, "#7cb5ec"],
					[0.5, "#7cb5ec80"],
					[0.9, "#7cb5ff33"]
				]
			},
			marker: { radius: 2 },
			lineWidth: 1,
			states: { hover: { lineWidth: 1 } },
			threshold: null
		}
	}
}

export default class SavedData extends Component {
    state = {
        user: {},
        jwt: "",
        data: {
            graphs: []
        }
    }

    async componentDidMount(){
        const user = auth.getCurrentUser();
        const jwt = auth.getJwt();
        const {data} = await crypto.getSavedData(user._id);
        console.log(data);
        this.setState({ user, jwt, data });
    }

    render() {
        return (
            <Layout>
                {this.state.loaded ?
                    <div></div>
				: <div className="w-50 mx-auto text-center" style={{paddingTop: "30vh"}}><img src={loading} /></div>}
            </Layout>
        )
    }
}
