import React, { Component } from 'react'

export default class Chart extends Component {
    state = {
        data: {}
    }

    componentDidMount(){
        this.setState({data:this.props.data})
    }

    render() {
        return (
            <div>
                <div className="col-md-3">
                    <div className="form-group">
                        <select name="Cryptocurrency" onChange={(e) => this.changeGraphCryptocurrency(e,this.state.index)} defaultValue={this.state.item.cryptocurrency} className="form-control">
                            {this.state.cryptocurrencies.map(i => (<option value={i.id} key={i.id}>{i.name} - {i.symbol}</option>))}
                        </select>
                        <select name="Currency" onChange={(e) => this.changeGraphCryptocurrency(e,this.state.index)} defaultValue={this.state.item.currency} className="form-control">
                            {this.state.currencies.map(i => (<option value={i.id} key={i.id}>{i.name} - {i.symbol}</option>))}
                        </select>
                        <input type="checkbox" onChange={(e) => this.changeGraphLive(e,this.state.index)} defaultValue={item.live} className="form-control"/>
                        <button className="btn btn-success" onClick={() => this.refreshSingle(this.state.index)}>REFRESH</button>
                        <button className="btn btn-warning" onClick={() => this.refreshSingle(this.state.index)}>SEARCH</button>
                        <button className="btn btn-danger" onClick={() => this.removeGraph(this.state.index)}>DELETE</button>
                        <button className="btn btn-primary" onClick={() => this.saveGraph(index)}>SAVE</button>
                    </div>
                </div>
                <div className="col-md-9 text-center">
                    {!this.state.loading ? <ReactHighcharts key={item.id} highcharts={Highcharts} config={item.chartOptions} ref="chart" oneToOne={true}/> : <img src={loading}/>}
                </div> 
            </div>
        )
    }
}
