import React, { Component } from 'react';
import Layout from "../components/layout";
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

import auth from "../services/authService";
import crypto from "../services/cryptoService";

export default class CustomData extends Component {
  state = {
    user: {},
    data: [],
    graphs: [],
    options: {}
  }
  
  async componentDidMount() {
	  const user = auth.getCurrentUser();
    this.setState({user});
    await crypto.getCurrencies().then(res => {
      const bkOptions = {...this.state.options};
      bkOptions.series = [{
        type: "area",
        name: "BTC to EUR",
        data: res.data
      }]
      this.setState({
        data: res.data,
        options: bkOptions
        })
    });
    //options.series[0].data = this.state.data;
    console.log(this.state.options);    console.log(this.state.data);
  }

  addGraph = () => {
      const graphs = [...this.state.graphs];
      graphs.push({
          id: 1,
          data: {
              name: "Bitcoin"
          }
      })
      this.setState({graphs});
  }

  render() {
    //const select = <select>{this.state.data.data.map(item => (<option value={item.id} key={item.id}>{item.name} - {item.symbol}</option>))}</select>

    return (
      <Layout>
          <p>Hello {this.state.user.name}</p>
          <button className="btn btn-primary" onClick={this.addGraph}>+</button>
          <div className="row">
            {this.state.graphs.length > 0 ? 
              this.state.graphs.map(item => <HighchartsReact key={item.id} highcharts={Highcharts} options={this.state.options} oneToOne={true}/>): ""}
            {/* {this.state.graphs.length > 0 ? 
                this.state.graphs.map(item => 
                    <div className="col-md-6" key={item.id}>
                        {select}
                        <p>{item.data.name}</p>
                        <HighchartsReact highcharts={Highcharts} options={this.state.options}/>
                    </div> 
            ): ""} */}
          </div>
      </Layout>
    )
  }
}
