import React, { Component } from 'react';
import Layout from "../components/layout";

import auth from "../services/authService";
import crypto from "../services/cryptoService";

export default class CustomData extends Component {
  state = {
    user: {},
    data: {
        data: [],
        timestamp: ""
    }
  }
  
  async componentDidMount() {
	const user = auth.getCurrentUser();
    this.setState({user});
    await crypto.getCurrencies().then(res => this.setState({data: res.data}));
    console.log(this.state.data);
  }

  render() {
    const select = <select>{this.state.data.data.map(item => (<option value={item.id} key={item.id}>{item.name} - {item.symbol}</option>))}
                </select>

    return (
      <Layout>
          <p>Hello {this.state.user.name}</p>
          {select}
      </Layout>
    )
  }
}
